import React, { useState, useEffect, useCallback } from 'react';
import { Link } from 'react-router-dom';
import { supabase } from '../../supabase';
import { LineChart, Line, XAxis, YAxis, CartesianGrid, Tooltip, ResponsiveContainer } from 'recharts';
import { useAuth } from '../../contexts/AuthContext';

const Dashboard = () => {
  const { user } = useAuth();
  const [stats, setStats] = useState({
    totalPosts: 0,
    publishedPosts: 0,
    draftPosts: 0,
    totalComments: 0,
    totalViews: 0,
    totalLikes: 0,
  });
  const [viewTrend, setViewTrend] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    fetchDashboardData();
  }, [fetchDashboardData]);

  const fetchDashboardData = useCallback(async () => {
    try {
      setLoading(true);
      setError(null);

      // Ëé∑ÂèñÁî®Êà∑ÁöÑÂçöÂÆ¢ÁªüËÆ°
      const { data: posts, error: postsError } = await supabase
        .from('posts')
        .select('*')
        .eq('author_id', user.id);

      if (postsError) throw postsError;

      const published = posts.filter(post => post.status === 'published');
      const drafts = posts.filter(post => post.status === 'draft');

      // Ëé∑ÂèñÁî®Êà∑ÁöÑËØÑËÆ∫ÁªüËÆ°
      const { data: comments, error: commentsError } = await supabase
        .from('comments')
        .select('*')
        .eq('user_id', user.id);

      if (commentsError) throw commentsError;

      // ËÆ°ÁÆóÊÄªÊµèËßàÈáèÂíåÁÇπËµûÊï∞
      const totalViews = published.reduce((sum, post) => sum + (post.view_count || 0), 0);
      const totalLikes = published.reduce((sum, post) => sum + (post.like_count || 0), 0);

      // ÁîüÊàêËøë7Â§©Ë∂ãÂäøÊï∞ÊçÆÔºàÊ®°ÊãüÊï∞ÊçÆÔºâ
      const trendData = [];
      for (let i = 6; i >= 0; i--) {
        const date = new Date();
        date.setDate(date.getDate() - i);
        trendData.push({
          date: date.toLocaleDateString('zh-CN', { month: 'short', day: 'numeric' }),
          views: Math.floor(Math.random() * 100) + 20, // Ê®°ÊãüÊï∞ÊçÆ
        });
      }

      setStats({
        totalPosts: posts.length,
        publishedPosts: published.length,
        draftPosts: drafts.length,
        totalComments: comments.length,
        totalViews,
        totalLikes,
      });
      setViewTrend(trendData);

    } catch (error) {
      setError(error.message);
    } finally {
      setLoading(false);
    }
  }, [user]);

  if (loading) {
    return (
      <div className="flex items-center justify-center h-64">
        <div className="animate-spin rounded-full h-12 w-12 border-b-2 border-primary-600"></div>
      </div>
    );
  }

  if (error) {
    return (
      <div className="bg-red-50 border border-red-200 rounded-md p-4">
        <div className="text-red-800">Âä†ËΩΩÂ§±Ë¥•: {error}</div>
      </div>
    );
  }

  return (
    <div>
      {/* È°µÈù¢Ê†áÈ¢ò */}
      <div className="mb-10">
        <div className="flex items-center space-x-4 mb-4">
          <div className="w-12 h-12 bg-gradient-to-br from-primary-500 to-primary-600 rounded-xl flex items-center justify-center shadow-lg">
            <svg className="w-6 h-6 text-white" fill="none" viewBox="0 0 24 24" stroke="currentColor">
              <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M9 19v-6a2 2 0 00-2-2H5a2 2 0 00-2 2v6a2 2 0 002 2h2a2 2 0 002-2zm0 0V9a2 2 0 012-2h2a2 2 0 012 2v10m-6 0a2 2 0 002 2h2a2 2 0 002-2m0 0V5a2 2 0 012-2h2a2 2 0 012 2v14a2 2 0 01-2 2h-2a2 2 0 01-2-2z" />
            </svg>
          </div>
          <div>
            <h1 className="text-3xl font-bold text-gray-900">‰∏™‰∫∫Êï∞ÊçÆÊ¶ÇËßà</h1>
            <p className="text-gray-600 mt-1">Êü•Áúã‰Ω†ÁöÑÂçöÂÆ¢Âàõ‰ΩúÊï∞ÊçÆÂíå‰∫íÂä®ÁªüËÆ°</p>
          </div>
        </div>
      </div>

      {/* ÁªüËÆ°Âç°Áâá */}
      <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-6 mb-10">
        <div className="bg-white rounded-xl shadow-sm border border-gray-100 p-6 hover:shadow-md transition-all duration-200">
          <div className="flex items-center">
            <div className="flex-shrink-0 bg-gradient-to-br from-primary-500 to-primary-600 rounded-xl p-3 shadow-lg">
              <span className="text-2xl">üìù</span>
            </div>
            <div className="ml-4">
              <p className="text-sm font-medium text-gray-500">ÂçöÂÆ¢ÊÄªÊï∞</p>
              <p className="text-3xl font-bold text-gray-900 mt-1">{stats.totalPosts}</p>
              <div className="flex items-center space-x-4 mt-2">
                <div className="flex items-center text-xs">
                  <div className="w-2 h-2 bg-green-500 rounded-full mr-1.5"></div>
                  <span className="text-green-600">Â∑≤ÂèëÂ∏É {stats.publishedPosts}</span>
                </div>
                <div className="flex items-center text-xs">
                  <div className="w-2 h-2 bg-gray-400 rounded-full mr-1.5"></div>
                  <span className="text-gray-500">ËçâÁ®ø {stats.draftPosts}</span>
                </div>
              </div>
            </div>
          </div>
        </div>

        <div className="bg-white rounded-xl shadow-sm border border-gray-100 p-6 hover:shadow-md transition-all duration-200">
          <div className="flex items-center">
            <div className="flex-shrink-0 bg-gradient-to-br from-blue-500 to-blue-600 rounded-xl p-3 shadow-lg">
              <span className="text-2xl">üí¨</span>
            </div>
            <div className="ml-4">
              <p className="text-sm font-medium text-gray-500">ËØÑËÆ∫ÊÄªÊï∞</p>
              <p className="text-3xl font-bold text-gray-900 mt-1">{stats.totalComments}</p>
              <p className="text-xs text-gray-500 mt-2">‰Ω†Âú®ÂçöÂÆ¢‰∏ãÁöÑËØÑËÆ∫</p>
            </div>
          </div>
        </div>

        <div className="bg-white rounded-xl shadow-sm border border-gray-100 p-6 hover:shadow-md transition-all duration-200">
          <div className="flex items-center">
            <div className="flex-shrink-0 bg-gradient-to-br from-green-500 to-green-600 rounded-xl p-3 shadow-lg">
              <span className="text-2xl">üëÄ</span>
            </div>
            <div className="ml-4">
              <p className="text-sm font-medium text-gray-500">ÊÄªÊµèËßàÈáè</p>
              <p className="text-3xl font-bold text-gray-900 mt-1">{stats.totalViews.toLocaleString()}</p>
              <p className="text-xs text-gray-500 mt-2">ÊâÄÊúâÂçöÂÆ¢ÁöÑÊµèËßàÊÄªÂíå</p>
            </div>
          </div>
        </div>

        <div className="bg-white rounded-xl shadow-sm border border-gray-100 p-6 hover:shadow-md transition-all duration-200">
          <div className="flex items-center">
            <div className="flex-shrink-0 bg-gradient-to-br from-red-500 to-red-600 rounded-xl p-3 shadow-lg">
              <span className="text-2xl">‚ù§Ô∏è</span>
            </div>
            <div className="ml-4">
              <p className="text-sm font-medium text-gray-500">ÊÄªÁÇπËµûÊï∞</p>
              <p className="text-3xl font-bold text-gray-900 mt-1">{stats.totalLikes}</p>
              <p className="text-xs text-gray-500 mt-2">ÊâÄÊúâÂçöÂÆ¢ÁöÑÁÇπËµûÊÄªÂíå</p>
            </div>
          </div>
        </div>
      </div>

      {/* Ëøë7Â§©ÊµèËßàË∂ãÂäø */}
      <div className="bg-white rounded-xl shadow-sm border border-gray-100 p-8 mb-10">
        <div className="flex items-center justify-between mb-6">
          <h2 className="text-xl font-bold text-gray-900">Ëøë7Â§©ÊµèËßàË∂ãÂäø</h2>
          <div className="flex items-center text-sm text-gray-500">
