import React from 'react';
import { Outlet, Link, useLocation } from 'react-router-dom';
import { useAuth } from '../contexts/AuthContext';

const AdminLayout = () => {
  const location = useLocation();
  const { profile } = useAuth();

  const isActiveLink = (path) => {
    return location.pathname === path;
  };

  const menuItems = [
    { path: '/admin', label: 'Êï∞ÊçÆÊ¶ÇËßà', icon: 'üìä' },
    { path: '/admin/posts', label: 'ÂçöÂÆ¢ÁÆ°ÁêÜ', icon: 'üìù' },
    { path: '/admin/comments', label: 'ËØÑËÆ∫ÁÆ°ÁêÜ', icon: 'üí¨' },
  ];

  return (
    <div className="flex min-h-screen bg-gray-50">
      <aside className="w-64 bg-white shadow-sm border-r border-gray-200">
        <div className="p-6">
          <h1 className="text-xl font-bold text-gray-900">ÁÆ°ÁêÜÂêéÂè∞</h1>
          <p className="text-sm text-gray-600 mt-1">
            Ê¨¢ËøéÂõûÊù•, {profile?.username}
          </p>
        </div>
        
        <nav className="px-4 pb-6">
          <ul className="space-y-2">
            {menuItems.map((item) => (
              <li key={item.path}>
                <Link
                  to={item.path}
                  className={`flex items-center px-4 py-3 text-sm font-medium rounded-lg transition-colors ${
                    isActiveLink(item.path)
                      ? 'bg-primary-50 text-primary-600 border-r-2 border-primary-600'
                      : 'text-gray-700 hover:bg-gray-50 hover:text-gray-900'
                  }`}
                >
                  <span className="mr-3 text-lg">{item.icon}</span>
                  {item.label}
                </Link>
              </li>
            ))}
          </ul>
        </nav>
      </aside>

      <main className="flex-1">
        <div className="p-8">
          <Outlet />
        </div>
      </main>
    </div>
  );
};

export default AdminLayout;
