import React from 'react';
import { Link, useLocation, useNavigate } from 'react-router-dom';
import { useAuth } from '../contexts/AuthContext';

const DashboardLayout = () => {
  const { user, logout } = useAuth();
  const location = useLocation();
  const navigate = useNavigate();

  const handleLogout = async () => {
    await logout();
    navigate('/');
  };

  const isActiveLink = (path) => {
    return location.pathname === path;
  };

  const menuItems = [
    { path: '/dashboard', label: 'Êï∞ÊçÆÊ¶ÇËßà', icon: 'üìä' },
    { path: '/dashboard/posts', label: 'ÊàëÁöÑÂçöÂÆ¢', icon: 'üìù' },
    { path: '/dashboard/comments', label: 'ÊàëÁöÑËØÑËÆ∫', icon: 'üí¨' },
  ];

  return (
    <div className="min-h-screen bg-gray-50">
      {/* È°∂ÈÉ®ÂØºËà™Ê†è */}
      <nav className="bg-white shadow-sm border-b border-gray-200">
        <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
          <div className="flex justify-between items-center h-16">
            <div className="flex items-center">
              <Link 
                to="/" 
                className="text-xl font-bold text-gray-900 hover:text-primary-600 transition-colors mr-8"
              >
                Â§öÁî®Êà∑ÂçöÂÆ¢Âπ≥Âè∞
              </Link>
              <span className="text-gray-600 font-medium">‰∏™‰∫∫‰∏≠ÂøÉ</span>
            </div>

            <div className="flex items-center space-x-4">
              <Link
                to="/"
                className="text-gray-600 hover:text-primary-600 px-3 py-2 rounded-md text-sm font-medium transition-colors"
              >
                ËøîÂõûÈ¶ñÈ°µ
              </Link>
              <Link
                to="/posts/new"
                className="bg-primary-600 text-white hover:bg-primary-700 px-4 py-2 rounded-md text-sm font-medium transition-colors"
              >
                ÂèëÂ∏ÉÂçöÂÆ¢
              </Link>
              <button
                onClick={handleLogout}
                className="text-gray-600 hover:text-red-600 px-3 py-2 rounded-md text-sm font-medium transition-colors"
              >
                ÈÄÄÂá∫ÁôªÂΩï
              </button>
            </div>
          </div>
        </div>
      </nav>

      <div className="flex">
        {/* ‰æßËæπÂØºËà™Ê†è */}
        <div className="w-64 bg-white shadow-sm border-r border-gray-200 min-h-screen">
          <div className="p-4">
            <div className="space-y-1">
              {menuItems.map((item) => (
                <Link
                  key={item.path}
                  to={item.path}
                  className={`flex items-center space-x-3 px-4 py-3 rounded-md text-sm font-medium transition-colors ${
                    isActiveLink(item.path)
                      ? 'bg-primary-50 text-primary-600 border-r-2 border-primary-600'
                      : 'text-gray-700 hover:bg-gray-50 hover:text-gray-900'
                  }`}
                >
                  <span className="text-lg">{item.icon}</span>
                  <span>{item.label}</span>
                </Link>
              ))}
            </div>
          </div>
        </div>

        {/* ‰∏ªÂÜÖÂÆπÂå∫Âüü */}
        <div className="flex-1">
          <main className="p-6">
            {/* Â≠êË∑ØÁî±Â∞ÜÂú®ËøôÈáåÊ∏≤Êüì */}
          </main>
        </div>
      </div>
    </div>
  );
};

export default DashboardLayout;
