import React, { useState, useEffect } from 'react';
import { useParams, Link, useNavigate } from 'react-router-dom';
import { useForm } from 'react-hook-form';
import ReactMarkdown from 'react-markdown';
import remarkGfm from 'remark-gfm';
import { supabase } from '../supabase';
import { useAuth } from '../contexts/AuthContext';

const BlogDetail = () => {
  const { id } = useParams();
  const { user, profile } = useAuth();
  const navigate = useNavigate();
  const [post, setPost] = useState(null);
  const [comments, setComments] = useState([]);
  const [loading, setLoading] = useState(true);
  const [commentsLoading, setCommentsLoading] = useState(false);
  const [submittingComment, setSubmittingComment] = useState(false);
  const [editingComment, setEditingComment] = useState(null);
  const [error, setError] = useState(null);

  const {
    register,
    handleSubmit,
    reset,
    formState: { errors },
  } = useForm();

  useEffect(() => {
    fetchPost();
    fetchComments();
  }, [id]);

  const fetchPost = async () => {
    try {
      const { data, error } = await supabase
        .from('posts')
        .select('*, profiles(username)')
        .eq('id', id)
        .eq('status', 'published')
        .single();

      if (error) throw error;

      setPost(data);
      
      await incrementViewCount(id);
    } catch (err) {
      console.error('Error fetching post:', err);
      setError('ÂçöÂÆ¢‰∏çÂ≠òÂú®ÊàñÂ∑≤Ë¢´Âà†Èô§');
      setLoading(false);
    }
  };

  const incrementViewCount = async (postId) => {
    try {
      const viewKey = `viewed_post_${postId}`;
      const lastViewed = localStorage.getItem(viewKey);
      const now = Date.now();
      
      if (!lastViewed || now - parseInt(lastViewed) > 24 * 60 * 60 * 1000) {
        await supabase
          .from('posts')
          .update({ view_count: (post?.view_count || 0) + 1 })
          .eq('id', postId);
        
        localStorage.setItem(viewKey, now.toString());
      }
    } catch (err) {
      console.error('Error incrementing view count:', err);
    }
  };

  const fetchComments = async () => {
    try {
      setCommentsLoading(true);
      const { data, error } = await supabase
        .from('comments')
        .select('*, profiles(username)')
        .eq('post_id', id)
        .order('created_at', { ascending: true });

      if (error) throw error;
      setComments(data || []);
    } catch (err) {
      console.error('Error fetching comments:', err);
    } finally {
      setCommentsLoading(false);
      setLoading(false);
    }
  };

  const onCommentSubmit = async (data) => {
    if (!user) {
      navigate('/login', { state: { from: location } });
      return;
    }

    try {
      setSubmittingComment(true);

      if (editingComment) {
        const { error } = await supabase
          .from('comments')
          .update({ content: data.content })
          .eq('id', editingComment.id);

        if (error) throw error;

        setComments(comments.map(comment => 
          comment.id === editingComment.id 
            ? { ...comment, content: data.content }
            : comment
        ));
        setEditingComment(null);
      } else {
        const { data: newComment, error } = await supabase
          .from('comments')
          .insert([
            {
              post_id: id,
              user_id: user.id,
              content: data.content,
            }
          ])
          .select('*, profiles(username)')
          .single();

        if (error) throw error;

        setComments([...comments, newComment]);
      }

      reset();
    } catch (err) {
      console.error('Error submitting comment:', err);
      setError('ËØÑËÆ∫Êèê‰∫§Â§±Ë¥•ÔºåËØ∑Á®çÂêéÈáçËØï');
    } finally {
      setSubmittingComment(false);
    }
  };

  const handleEditComment = (comment) => {
    setEditingComment(comment);
    reset({ content: comment.content });
  };

  const handleDeleteComment = async (commentId) => {
    if (!window.confirm('Á°ÆÂÆöË¶ÅÂà†Èô§ËøôÊù°ËØÑËÆ∫ÂêóÔºü')) return;

    try {
      const { error } = await supabase
        .from('comments')
        .delete()
        .eq('id', commentId);

      if (error) throw error;

      setComments(comments.filter(comment => comment.id !== commentId));
    } catch (err) {
      console.error('Error deleting comment:', err);
      setError('Âà†Èô§ËØÑËÆ∫Â§±Ë¥•ÔºåËØ∑Á®çÂêéÈáçËØï');
    }
  };

  const canEditComment = (comment) => {
    return user && (comment.user_id === user.id || profile?.role === 'blogger');
  };

  const formatDate = (dateString) => {
    const date = new Date(dateString);
    return date.toLocaleDateString('zh-CN', {
      year: 'numeric',
      month: 'long',
      day: 'numeric',
      hour: '2-digit',
      minute: '2-digit',
    });
  };

  if (loading) {
    return (
      <div className="max-w-4xl mx-auto px-4 sm:px-6 lg:px-8 py-8">
        <div className="animate-pulse">
          <div className="skeleton skeleton-title mb-4"></div>
          <div className="skeleton skeleton-line mb-8"></div>
          <div className="space-y-4">
            <div className="skeleton skeleton-line"></div>
            <div className="skeleton skeleton-line"></div>
            <div className="skeleton skeleton-line w-2/3"></div>
          </div>
        </div>
      </div>
    );
  }

  if (error || !post) {
    return (
      <div className="max-w-4xl mx-auto px-4 sm:px-6 lg:px-8 py-8">
        <div className="text-center">
          <div className="text-gray-400 text-6xl mb-4">üòï</div>
          <h2 className="text-xl font-medium text-gray-900 mb-2">
            {error || 'ÂçöÂÆ¢‰∏çÂ≠òÂú®'}
          </h2>
          <Link
            to="/"
            className="text-primary-600 hover:text-primary-500"
          >
            ËøîÂõûÈ¶ñÈ°µ
          </Link>
        </div>
      </div>
    );
  }

  return (
    <div className="max-w-4xl mx-auto px-4 sm:px-6 lg:px-8 py-8">
      <article>
        <header className="mb-8">
          <h1 className="text-3xl font-bold text-gray-900 mb-4 text-center">
            {post.title}
          </h1>
          <div className="flex flex-col sm:flex-row items-center justify-between text-sm text-gray-500 text-center">
            <div className="flex items-center space-x-4 mb-2 sm:mb-0">
              <span>
                ‰ΩúËÄÖÔºö{post.profiles?.username || 'Âçö‰∏ª'}
              </span>
              <span>
                ÂèëÂ∏ÉÊó∂Èó¥Ôºö{formatDate(post.created_at)}
              </span>
            </div>
            <div className="flex items-center space-x-4">
              <span className="inline-flex items-center">
                <svg
                  className="w-4 h-4 mr-1"
                  fill="none"
                  viewBox="0 0 24 24"
                  stroke="currentColor"
                >
                  <path
                    strokeLinecap="round"
                    strokeLinejoin="round"
                    strokeWidth={2}
                    d="M15 12a3 3 0 11-6 0 3 3 0 016 0z"
                  />
                  <path
                    strokeLinecap="round"
                    strokeLinejoin="round"
                    strokeWidth={2}
                    d="M2.458 12C3.732 7.943 7.523 5 12 5c4.478 0 8.268 2.943 9.542 7-1.274 4.057-5.064 7-9.542 7-4.477 0-8.268-2.943-9.542-7z"
                  />
                </svg>
                {post.view_count || 0} Ê¨°ÈòÖËØª
              </span>
              {post.like_count > 0 && (
                <span className="inline-flex items-center">
                  <svg
                    className="w-4 h-4 mr-1"
                    fill="currentColor"
                    viewBox="0 0 24 24"
                  >
                    <path d="M12 21.35l-1.45-1.32C5.4 15.36 2 12.28 2 8.5 2 5.42 4.42 3 7.5 3c1.74 0 3.41.81 4.5 2.09C13.09 3.81 14.76 3 16.5 3 19.58 3 22 5.42 22 8.5c0 3.78-3.4 6.86-8.55 11.54L12 21.35z"/>
                  </svg>
                  {post.like_count} ‰∏™Ëµû
                </span>
              )}
            </div>
          </div>
        </header>

        <div className="bg-white rounded-lg shadow-sm border border-gray-200 p-6 mb-8">
          <div className="markdown-content">
            <ReactMarkdown remarkPlugins={[remarkGfm]}>
              {post.content}
            </ReactMarkdown>
          </div>
        </div>

        <section>
          <h2 className="text-2xl font-semibold text-gray-900 mb-6">
            ËØÑËÆ∫Âå∫ ({comments.length})
          </h2>

          {user ? (
            <div className="bg-white rounded-lg shadow-sm border border-gray-200 p-6 mb-6">
              <h3 className="text-lg font-medium text-gray-900 mb-4">
                {editingComment ? 'ÁºñËæëËØÑËÆ∫' : 'ÂèëË°®ËØÑËÆ∫'}
              </h3>
              <form onSubmit={handleSubmit(onCommentSubmit)}>
                <div className="mb-4">
                  <textarea
                    {...register('content', {
                      required: 'ËØ∑ËæìÂÖ•ËØÑËÆ∫ÂÜÖÂÆπ',
                      minLength: {
                        value: 1,
                        message: 'ËØÑËÆ∫ÂÜÖÂÆπ‰∏çËÉΩ‰∏∫Á©∫',
                      },
                      maxLength: {
                        value: 500,
                        message: 'ËØÑËÆ∫ÂÜÖÂÆπ‰∏çËÉΩË∂ÖËøá500Â≠ó',
                      },
                    })}
                    rows="4"
                    className="w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-primary-500 focus:border-transparent"
                    placeholder="ÂÜô‰∏ã‰Ω†ÁöÑËØÑËÆ∫..."
                  />
                  {errors.content && (
                    <p className="mt-1 text-sm text-red-600">{errors.content.message}</p>
                  )}
                </div>
                <div className="flex space-x-4">
                  <button
                    type="submit"
                    disabled={submittingComment}
                    className="btn btn-primary"
                  >
                    {submittingComment ? 'Êèê‰∫§‰∏≠...' : editingComment ? 'Êõ¥Êñ∞ËØÑËÆ∫' : 'ÂèëË°®ËØÑËÆ∫'}
                  </button>
                  {editingComment && (
                    <button
                      type="button"
                      onClick={() => {
                        setEditingComment(null);
                        reset();
                      }}
                      className="btn btn-secondary"
                    >
                      ÂèñÊ∂à
                    </button>
                  )}
                </div>
              </form>
            </div>
          ) : (
            <div className="bg-gray-50 rounded-lg border border-gray-200 p-6 mb-6 text-center">
              <p className="text-gray-600 mb-4">
                ÁôªÂΩïÂêéÊâçËÉΩÂèëË°®ËØÑËÆ∫
              </p>
              <Link
                to="/login"
                state={{ from: location }}
                className="btn btn-primary"
              >
                Á´ãÂç≥ÁôªÂΩï
              </Link>
            </div>
          )}

          {commentsLoading ? (
            <div className="animate-pulse">
              {[...Array(3)].map((_, i) => (
                <div key={i} className="bg-white rounded-lg shadow-sm border border-gray-200 p-6 mb-4">
                  <div className="skeleton skeleton-title mb-2"></div>
                  <div className="skeleton skeleton-line mb-2"></div>
                  <div className="skeleton skeleton-line w-3/4"></div>
                </div>
              ))}
            </div>
          ) : comments.length === 0 ? (
            <div className="text-center py-8 bg-white rounded-lg border border-gray-200">
              <p className="text-gray-500">ÊöÇÊó†ËØÑËÆ∫ÔºåÂø´Êù•ÂèëË°®Á¨¨‰∏ÄÊù°ËØÑËÆ∫ÂêßÔºÅ</p>
            </div>
          ) : (
            <div className="space-y-4">
              {comments.map((comment) => (
                <div
                  key={comment.id}
                  className="bg-white rounded-lg shadow-sm border border-gray-200 p-6"
                >
                  <div className="flex justify-between items-start mb-3">
                    <div className="flex items-center space-x-3">
                      <div className="w-10 h-10 bg-primary-100 rounded-full flex items-center justify-center">
                        <span className="text-primary-600 font-medium">
                          {comment.profiles?.username?.charAt(0)?.toUpperCase() || 'U'}
                        </span>
                      </div>
                      <div>
                        <h4 className="font-medium text-gray-900">
                          {comment.profiles?.username || 'ÂåøÂêçÁî®Êà∑'}
                        </h4>
                        <p className="text-sm text-gray-500">
                          {formatDate(comment.created_at)}
                        </p>
                      </div>
                    </div>
                    {canEditComment(comment) && (
                      <div className="flex space-x-2">
                        <button
                          onClick={() => handleEditComment(comment)}
                          className="text-sm text-gray-500 hover:text-primary-600"
                        >
                          ÁºñËæë
                        </button>
                        <button
                          onClick={() => handleDeleteComment(comment.id)}
                          className="text-sm text-gray-500 hover:text-red-600"
                        >
                          Âà†Èô§
                        </button>
                      </div>
                    )}
                  </div>
                  <div className="prose prose-sm max-w-none">
                    <ReactMarkdown remarkPlugins={[remarkGfm]}>
                      {comment.content}
                    </ReactMarkdown>
                  </div>
                </div>
              ))}
            </div>
          )}
        </section>
      </article>
    </div>
  );
};

export default BlogDetail;
