import React, { useState, useEffect } from 'react';
import { useNavigate, useParams } from 'react-router-dom';
import { useForm } from 'react-hook-form';
import { supabase } from '../../supabase';
import ReactMarkdown from 'react-markdown';
import remarkGfm from 'remark-gfm';
import { useAuth } from '../../contexts/AuthContext';

const PostEditor = () => {
  const { user } = useAuth();
  const { id } = useParams();
  const navigate = useNavigate();
  const isEditing = Boolean(id);
  
  const [loading, setLoading] = useState(true);
  const [saving, setSaving] = useState(false);
  const [error, setError] = useState(null);
  const [activeTab, setActiveTab] = useState('edit');
  const [formData, setFormData] = useState({
    title: '',
    summary: '',
    content: '',
  });

  const {
    register,
    handleSubmit,
    formState: { errors },
    setValue,
    watch,
    reset,
  } = useForm();

  const watchedData = watch();

  useEffect(() => {
    if (isEditing) {
      fetchPost();
    } else {
      setLoading(false);
    }
  }, [id]);

  useEffect(() => {
    // 从localStorage加载草稿（仅限新建博客时）
    const savedData = localStorage.getItem('postDraft');
    if (savedData && !isEditing) {
      const draft = JSON.parse(savedData);
      setFormData(draft);
      setValue('title', draft.title);
      setValue('summary', draft.summary);
      setValue('content', draft.content);
    }
  }, [setValue, isEditing]);

  // 自动保存草稿
  useEffect(() => {
    if (!isEditing && (watchedData.title || watchedData.content)) {
      const draftData = {
        title: watchedData.title || '',
        summary: watchedData.summary || '',
        content: watchedData.content || '',
      };
      localStorage.setItem('postDraft', JSON.stringify(draftData));
    }
  }, [watchedData, isEditing]);

  const fetchPost = async () => {
    try {
      const { data, error } = await supabase
        .from('posts')
        .select('*')
        .eq('id', id)
        .eq('author_id', user.id)
        .single();

      if (error) throw error;

      setFormData({
        title: data.title,
        summary: data.summary || '',
        content: data.content,
      });
      
      setValue('title', data.title);
      setValue('summary', data.summary || '');
      setValue('content', data.content);

    } catch (error) {
      setError(error.message);
    } finally {
      setLoading(false);
    }
  };

  const onSubmit = async (data, status) => {
    try {
      setSaving(true);
      setError(null);

      const postData = {
        title: data.title.trim(),
        summary: data.summary?.trim() || '',
        content: data.content.trim(),
        status,
      };

      let result;
      if (isEditing) {
        result = await supabase
          .from('posts')
          .update(postData)
          .eq('id', id)
          .eq('author_id', user.id);
      } else {
        result = await supabase
          .from('posts')
          .insert({
            ...postData,
            author_id: user.id,
            view_count: 0,
            like_count: 0,
          });
      }

      if (result.error) throw result.error;

      // 清除草稿
      localStorage.removeItem('postDraft');

      if (status === 'published') {
        navigate('/dashboard/posts');
      } else {
        navigate(isEditing ? '/dashboard/posts' : '/dashboard/posts');
      }

    } catch (error) {
      setError(error.message);
    } finally {
      setSaving(false);
    }
  };

  const handleSaveDraft = (data) => {
    onSubmit(data, 'draft');
  };

  const handlePublish = (data) => {
    if (!window.confirm('确定要发布这篇博客吗？')) {
      return;
    }
    onSubmit(data, 'published');
  };

  if (loading) {
    return (
      <div className="flex items-center justify-center h-64">
        <div className="animate-spin rounded-full h-12 w-12 border-b-2 border-primary-600"></div>
      </div>
    );
  }

  if (error && isEditing) {
    return (
      <div className="bg-red-50 border border-red-200 rounded-md p-4">
        <div className="text-red-800">加载失败: {error}</div>
      </div>
    );
  }

  return (
    <div>
      {/* 页面标题 */}
      <div className="mb-6">
        <h1 className="text-3xl font-bold text-gray-900">
          {isEditing ? '编辑博客' : '发布新博客'}
        </h1>
        <p className="text-gray-600 mt-2">
          {isEditing ? '修改博客内容' : '分享你的想法和见解'}
        </p>
      </div>

      {/* 编辑器表单 */}
      <div className="bg-white rounded-lg shadow-sm border border-gray-200 p-6">
        <form className="space-y-6">
          {/* 标题输入 */}
          <div>
            <label className="block text-sm font-medium text-gray-700 mb-2">
              博客标题 *
            </label>
            <input
              type="text"
              {...register('title', {
                required: '请输入博客标题',
                maxLength: {
                  value: 100,
                  message: '标题不能超过100个字符'
                }
              })}
              className="input"
              placeholder="输入吸引人的标题..."
            />
            {errors.title && (
              <p className="mt-1 text-sm text-red-600">{errors.title.message}</p>
            )}
          </div>

          {/* 摘要输入 */}
          <div>
            <label className="block text-sm font-medium text-gray-700 mb-2">
              博客摘要
            </label>
            <textarea
              {...register('summary', {
                maxLength: {
                  value: 200,
                  message: '摘要不能超过200个字符'
                }
              })}
              rows={3}
              className="input resize-none"
              placeholder="简要描述博客内容..."
            />
            {errors.summary && (
              <p className="mt-1 text-sm text-red-600">{errors.summary.message}</p>
            )}
          </div>

          {/* 内容编辑器 */}
          <div>
            <label className="block text-sm font-medium text-gray-700 mb-2">
              博客内容 *
            </label>
            
            {/* 标签切换 */}
            <div className="flex space-x-4 mb-4">
              <button
                type="button"
                onClick={() => setActiveTab('edit')}
                className={`px-4 py-2 rounded-md text-sm font-medium transition-colors ${
                  activeTab === 'edit'
                    ? 'bg-primary-100 text-primary-700 border-primary-300'
                    : 'bg-gray-100 text-gray-700 hover:bg-gray-200'
                }`}
              >
                编辑
              </button>
              <button
                type="button"
                onClick={() => setActiveTab('preview')}
                className={`px-4 py-2 rounded-md text-sm font-medium transition-colors ${
                  activeTab === 'preview'
                    ? 'bg-primary-100 text-primary-700 border-primary-300'
                    : 'bg-gray-100 text-gray-700 hover:bg-gray-200'
                }`}
              >
                预览
              </button>
            </div>

            {/* 编辑器 */}
            <div className="border border-gray-300 rounded-md overflow-hidden">
              {activeTab === 'edit' ? (
                <textarea
                  {...register('content', {
                    required: '请输入博客内容',
                    minLength: {
                      value: 10,
                      message: '内容至少需要10个字符'
                    }
                  })}
                  rows={20}
                  className="w-full px-3 py-2 border-0 focus:ring-0 focus:border-transparent resize-none"
                  placeholder="使用Markdown语法编写博客内容..."
                />
              ) : (
                <div className="max-h-96 overflow-y-auto p-4 bg-gray-50">
                  <div className="prose prose-sm max-w-none">
                    <ReactMarkdown remarkPlugins={[remarkGfm]}>
                      {watchedData.content || ''}
                    </ReactMarkdown>
                  </div>
                </div>
              )}
            </div>
            
            {errors.content && (
              <p className="mt-1 text-sm text-red-600">{errors.content.message}</p>
            )}
          </div>

          {/* 错误提示 */}
          {error && (
            <div className="bg-red-50 border border-red-200 rounded-md p-4">
              <div className="text-red-800">{error}</div>
            </div>
          )}

          {/* 操作按钮 */}
          <div className="flex justify-end space-x-4">
            <button
              type="button"
              onClick={() => navigate('/dashboard/posts')}
              className="px-4 py-2 text-gray-700 hover:text-gray-900 font-medium transition-colors"
            >
              取消
            </button>
            <button
              type="button"
              onClick={handleSubmit(handleSaveDraft)}
              disabled={saving}
              className="px-4 py-2 bg-gray-200 text-gray-800 hover:bg-gray-300 rounded-md font-medium transition-colors disabled:opacity-50"
            >
              {saving ? '保存中...' : '保存草稿'}
            </button>
            <button
              type="button"
              onClick={handleSubmit(handlePublish)}
              disabled={saving}
              className="px-6 py-2 bg-primary-600 text-white hover:bg-primary-700 rounded-md font-medium transition-colors disabled:opacity-50"
            >
              {saving ? '发布中...' : '发布博客'}
            </button>
          </div>
        </form>
      </div>
    </div>
  );
};

export default PostEditor;
