import React, { useState, useEffect } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import { supabase } from '../../supabase';
import { useAuth } from '../../contexts/AuthContext';

const Posts = () => {
  const { user } = useAuth();
  const navigate = useNavigate();
  const [posts, setPosts] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [statusFilter, setStatusFilter] = useState('all');
  const [deletingPost, setDeletingPost] = useState(null);

  useEffect(() => {
    fetchPosts();
  }, [statusFilter]);

  const fetchPosts = async () => {
    try {
      setLoading(true);
      setError(null);

      let query = supabase
        .from('posts')
        .select('*')
        .eq('author_id', user.id)
        .order('created_at', { ascending: false });

      if (statusFilter !== 'all') {
        query = query.eq('status', statusFilter);
      }

      const { data, error: fetchError } = await query;

      if (fetchError) throw fetchError;

      setPosts(data || []);

    } catch (error) {
      setError(error.message);
    } finally {
      setLoading(false);
    }
  };

  const handleDeletePost = async (postId) => {
    if (!window.confirm('Á°ÆÂÆöË¶ÅÂà†Èô§ËøôÁØáÂçöÂÆ¢ÂêóÔºüÊ≠§Êìç‰ΩúÊó†Ê≥ïÊí§ÈîÄ„ÄÇ')) {
      return;
    }

    try {
      setDeletingPost(postId);
      
      const { error } = await supabase
        .from('posts')
        .delete()
        .eq('id', postId);

      if (error) throw error;

      setPosts(posts.filter(post => post.id !== postId));

    } catch (error) {
      setError(error.message);
    } finally {
      setDeletingPost(null);
    }
  };

  const formatDate = (dateString) => {
    return new Date(dateString).toLocaleDateString('zh-CN', {
      year: 'numeric',
      month: 'long',
      day: 'numeric',
      hour: '2-digit',
      minute: '2-digit'
    });
  };

  if (loading) {
    return (
      <div className="flex items-center justify-center h-64">
        <div className="animate-spin rounded-full h-12 w-12 border-b-2 border-primary-600"></div>
      </div>
    );
  }

  if (error) {
    return (
      <div className="bg-red-50 border border-red-200 rounded-md p-4">
        <div className="text-red-800">Âä†ËΩΩÂ§±Ë¥•: {error}</div>
      </div>
    );
  }

  return (
    <div>
      {/* È°µÈù¢Ê†áÈ¢òÂíåÊìç‰ΩúÊ†è */}
      <div className="mb-6">
        <div className="flex justify-between items-center">
          <div>
            <h1 className="text-3xl font-bold text-gray-900">ÊàëÁöÑÂçöÂÆ¢</h1>
            <p className="text-gray-600 mt-2">ÁÆ°ÁêÜ‰Ω†ÂèëÂ∏ÉÁöÑÊâÄÊúâÂçöÂÆ¢</p>
          </div>
          <Link
            to="/posts/new"
            className="bg-primary-600 text-white hover:bg-primary-700 px-4 py-2 rounded-md font-medium transition-colors flex items-center"
          >
            <span className="mr-2">‚ûï</span>
            ÂèëÂ∏ÉÊñ∞ÂçöÂÆ¢
          </Link>
        </div>
      </div>

      {/* Áä∂ÊÄÅÁ≠õÈÄâÂô® */}
      <div className="bg-white rounded-lg shadow-sm border border-gray-200 p-4 mb-6">
        <div className="flex items-center space-x-4">
          <span className="text-sm font-medium text-gray-700">Áä∂ÊÄÅÁ≠õÈÄâ:</span>
          <div className="flex space-x-2">
            {[
              { value: 'all', label: 'ÂÖ®ÈÉ®', color: 'gray' },
              { value: 'published', label: 'Â∑≤ÂèëÂ∏É', color: 'green' },
              { value: 'draft', label: 'ËçâÁ®ø', color: 'yellow' }
            ].map((filter) => (
              <button
                key={filter.value}
                onClick={() => setStatusFilter(filter.value)}
                className={`px-3 py-1 rounded-md text-sm font-medium transition-colors ${
                  statusFilter === filter.value
                    ? `bg-${filter.color}-100 text-${filter.color}-800 border-${filter.color}-300`
                    : 'bg-gray-100 text-gray-600 hover:bg-gray-200'
                }`}
              >
                {filter.label}
              </button>
            ))}
          </div>
        </div>
      </div>

      {/* ÂçöÂÆ¢ÂàóË°® */}
      {posts.length === 0 ? (
        <div className="bg-white rounded-lg shadow-sm border border-gray-200 p-8 text-center">
          <div className="text-gray-400 text-lg mb-4">üìù</div>
          <h3 className="text-lg font-medium text-gray-900 mb-2">
            {statusFilter === 'all' ? 'ËøòÊ≤°ÊúâÂçöÂÆ¢' : 
             statusFilter === 'published' ? 'ËøòÊ≤°ÊúâÂ∑≤ÂèëÂ∏ÉÁöÑÂçöÂÆ¢' : 'ËøòÊ≤°ÊúâËçâÁ®ø'}
          </h3>
          <p className="text-gray-600 mb-4">
            {statusFilter === 'all' ? 'ÂºÄÂßãÂàõ‰Ωú‰Ω†ÁöÑÁ¨¨‰∏ÄÁØáÂçöÂÆ¢ÂêßÔºÅ' :
             statusFilter === 'published' ? 'ÂèëÂ∏É‰Ω†ÁöÑÂçöÂÆ¢ËÆ©Êõ¥Â§ö‰∫∫ÁúãÂà∞ÂêßÔºÅ' : '‰øùÂ≠òËçâÁ®ø‰ª•‰æøÁ®çÂêéÁºñËæë'}
          </p>
          {statusFilter !== 'published' && (
            <Link
              to="/posts/new"
              className="bg-primary-600 text-white hover:bg-primary-700 px-4 py-2 rounded-md font-medium transition-colors inline-block"
            >
              ÂèëÂ∏ÉÊñ∞ÂçöÂÆ¢
            </Link>
          )}
        </div>
      ) : (
        <div className="bg-white rounded-lg shadow-sm border border-gray-200">
          <div className="overflow-x-auto">
            <table className="min-w-full divide-y divide-gray-200">
              <thead className="bg-gray-50">
                <tr>
                  <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                    Ê†áÈ¢ò
                  </th>
                  <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                    Áä∂ÊÄÅ
                  </th>
                  <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                    ÂèëÂ∏ÉÊó∂Èó¥
                  </th>
                  <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
